    {
        var secondsToGo = 5;
        var props = new ConfirmOptions()
        {
            Title = "This is a notification message",
            Content = $"This modal will be destroyed after {secondsToGo} second."
        };
        _modalService.Success(props);
        while (secondsToGo > 0)
        {
            secondsToGo -= 1;
            await Task.Delay(1000);
