@code
{
    private AutoComplete _autoComplete;
    private string txtValue;

    private void OnChange(string value)
    {
        var list = string.IsNullOrWhiteSpace(value) ? Array.Empty<string>() : new[]
        {
            value,
            value + value,
            value + value + value
        };
        _autoComplete.LoadData(list);
    }
}

